public  class Multiply 
{
	public static void main(String[] args)
	{

		int m1 =4;
		int m2 =6;
		int result = multiply(m1,m2);
		System.out.println("Result : "+ result);

	}

	public static int multiply(int a, int b) 
	{
		if(a==0) 
			return 0;
		int result =0;
		result = multiply(a, b-1);
		result = result +a;
		return result;



	}

}
