def longest_repeated_substring_length(s: str) -> int:
	n = len(s)
	dp = [[0] * (n + 1) for _ in range(n + 1)]
	res = 0
	for i in range(1, n + 1):
		for j in range(i + 1, n + 1):
			if s[i - 1] == s[j - 1] and dp[i - 1][j - 1] < j - i:
				dp[i][j] = dp[i - 1][j - 1] + 1
				if dp[i][j] > res:
					res = dp[i][j]
			else:
				dp[i][j] = 0
	return res
