def basic_calculator(s):
    num = 0
    stack = []
    sign = "+"
    for i in range(len(s)):
        if s[i].isdigit():
            num = num*10 + int(s[i])
        if i==len(s)-1 or (s[i] in "+-*/"):
            if sign=="+":
                stack.append(num)
            elif sign=="-":
                stack.append(-num)
            elif sign=="*":
                stack[-1] = stack[-1]*num
            else:
                stack[-1] = int(stack[-1]/num)
            num = 0
            sign = s[i]
    return sum(stack)
