def is_one_edit_distance(s1: str, s2: str) -> bool:
    n1, n2 = len(s1), len(s2)
    if abs(n1 - n2) > 1:
        return False
    if s1 == s2:
        return True
    for i in range(min(n1, n2)):
        if s1[i] != s2[i]:
            if n1 == n2:
                return s1[i+1:] == s2[i+1:]
            elif n1 < n2:
                return s1[i:] == s2[i+1:]
            else:
                return s1[i+1:] == s2[i:]
    return abs(n1 - n2) == 1
