def longest_substring_k_distinct(s, k):
    if k == 0 or s == "":
        return ''

    window_start = 0
    max_len = 0
    max_substring_start = 0
    chars_freq = {}

    for window_end in range(len(s)):
        right_char = s[window_end]
        if right_char not in chars_freq:
            chars_freq[right_char] = 0
        chars_freq[right_char] += 1

        while len(chars_freq) > k:
            left_char = s[window_start]
            chars_freq[left_char] -= 1
            if chars_freq[left_char] == 0:
                del chars_freq[left_char]
            window_start += 1

        if window_end - window_start + 1 > max_len:
            max_len = window_end - window_start + 1
            max_substring_start = window_start

    return s[max_substring_start:max_substring_start + max_len]
