def max_balloons(text):
    # Initialize a dictionary to hold the character counts
    char_count = {'b': 0, 'a': 0, 'l': 0, 'o': 0, 'n': 0}

    # Count the characters in the text
    for char in text:
        if char in char_count:
            char_count[char] += 1
    # Half the counts of the 'l' and 'o' characters
    char_count['l'] //= 2
    char_count['o'] //= 2

    # Return the minimum character count, as this represents the maximum number of 'balloon's we can form
    return min(char_count.values())
