from typing import List

def max_sub_array_len(nums: List[int], k: int) -> int:   
    sum_map = {0:-1}
    max_len = cur_sum = 0
    for i, num in enumerate(nums):
        cur_sum += num
        if cur_sum not in sum_map:
            sum_map[cur_sum] = i
        if cur_sum - k in sum_map:
            max_len = max(max_len, i - sum_map[cur_sum - k])
    return max_len
