from typing import List, Union, NestedInteger
def depth_sum(nested_list: List[NestedInteger], depth=1) -> int:
    total = 0
    for element in nested_list:
        if isinstance(element, list):
            # If the item is a list, make a recursive call with an increased depth.
            total += depth_sum(element, depth + 1)
        else:
            # If the item is an integer, multiply it by the depth and add it to the sum.
            total += element * depth     
    return total
