def remove_kdigits(num, k):
    stack = []
    for n in num:
        while k and stack and stack[-1] > n:
            stack.pop()
            k -= 1
        stack.append(n)
    while k:
        stack.pop()
        k -= 1
    output = ''.join(stack).lstrip('0')
    if output:
        return output
    else:
        return '0'
