def shortest_distance(words, word1, word2):
    idx1, idx2 = -1, -1
    min_distance = len(words)

    for i, word in enumerate(words):
        if word == word1:
            idx1 = i
        elif word == word2:
            idx2 = i

        if idx1 != -1 and idx2 != -1:
            min_distance = min(min_distance, abs(idx1 - idx2))
    return min_distance
