from collections import Counter
import heapq

def least_interval(tasks, n):

    # build a max heap
    heap = [-freq for task, freq in Counter(tasks).items()]
    heapq.heapify(heap)

    time = 0
    while heap:
        i, temp = 0, []
        while i <= n:
            if heap:
                if heap[0] < -1:
                    heapq.heappush(temp, heapq.heappop(heap) + 1)
                else:
                    heapq.heappop(heap)

            time += 1
            if not heap and not temp:
                break

            if i <= n:
                i += 1

        for freq in temp:
            heapq.heappush(heap, freq)

    return str(time)
