from collections import defaultdict

def vertical_order(root):

    def dfs(node, col):

        if node is None:
            return

        rows[col].append(node.val)

        dfs(node.left, col-1)
        dfs(node.right, col+1)

    if root is None:
        return []

    rows = defaultdict(list)

    dfs(root, 0)

    return [rows[i] for i in sorted(rows.keys())]
